import UIKit
import TMDBSwift
import PromiseKit

class MovieDetailSceneWorker {
    
    func getMovieDetail(movieId: Int) -> Promise<MovieDetailedMDB> {
        return Promise { seal in
            MovieMDB.movie(movieID: movieId) { (result, movie) in
                guard let movie = movie else {
                    if let error = result.error {
                        seal.reject(error)
                    }
                    return
                }
                seal.fulfill(movie)
            }
        }
    }
    
    func getCredits(movieId: Int) -> Promise<MovieCreditsMDB> {
        return Promise { seal in
            MovieMDB.credits(movieID: movieId) { (result, credits) in
                guard let credits = credits else {
                    if let error = result.error {
                        seal.reject(error)
                    }
                    return
                }
                seal.fulfill(credits)
            }
        }
    }
}
